import * as THREE from "https://unpkg.com/three@0.121.1/build/three.module.js";
import { Mundo } from "./Mundo.js";
import { Provincia } from "./Provincia.js";

let mundo;
let data = [];
let provincias = [];

const raycaster = new THREE.Raycaster();
const mouse = new THREE.Vector2();

function inicializar() {
  cargarDatos(data);

  /* Mundo */
  mundo = new Mundo();
  mundo.crearFondo();

  console.log(data[0]);
  data.forEach((provincia) => {
    let prov = new Provincia(provincia);
    provincias.push(prov);
    mundo.agregar(prov.crearFigura(provincia.offset));
    mundo.agregar(prov.crearLinea(provincia.offset));
  });
  /*let jujuy = new Provincia(data[0]);
  provincias.push(jujuy);
  mundo.agregar(jujuy.crearFigura(0,0,1));
  let salta = new Provincia(data[1]);
  provincias.push(salta);
  mundo.agregar(salta.crearFigura(22,-16,1));*/

  window.addEventListener("click", toqueProvincia);
  document.addEventListener("mousemove", onPointerMove);
}

function animar() {
  requestAnimationFrame(animar);

  //
  mundo.renderizar();
}

/* CÃ³digo principal */
inicializar();
animar();

/***** funciones ****/
function onPointerMove(event) {
  mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
  mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;

  //console.log(mouse);
}
function toqueProvincia() {
  raycaster.setFromCamera(mouse, mundo.camara);
  let intersection = raycaster.intersectObjects(mundo.escena.children);
  if (intersection.length > 0) {
    console.log("toque");

    provincias.forEach((prov) => {
      prov.seleccionar(intersection[0].object.id);
    });
  }
}
